          )}

          {currentPage === 'profile' && (
            <div className="animate-in slide-in-from-bottom duration-300">
              <div className="flex justify-between items-center mb-6">
                <div className="flex gap-3">
                  <button onClick={() => { setCurrentPage('home'); setIsEditingProfile(false); }} className="text-slate-400 font-bold py-2 px-4 bg-slate-100 hover:bg-slate-200 rounded-lg transition">â† Back</button>
                </div>
                <div className="flex gap-3">
                  <button onClick={() => { setIsEditingProfile(prev => !prev); }} className="text-white font-bold py-2 px-4 bg-red-600 hover:bg-red-700 rounded-lg transition">{isEditingProfile ? 'Cancel Edit' : 'Edit Profile'}</button>
                </div>
              </div>

              <h2 className="text-3xl font-black mb-6">Your Profile</h2>

              {isEditingProfile ? (
                <EditProfileForm
                  initialData={userData}
                  onSave={(updated) => {
                    localStorage.setItem('userData', JSON.stringify(updated));
